// e2e/tests/prompt-editing.spec.ts
import { test, expect } from '@playwright/test';
import { CreateReportPage } from '../pages/create-report.page';
import { interceptReportCreationRequest } from '../utils/api-interceptor';

test('edited prompts should be correctly sent in API request', async ({ page }) =&gt; {
  const createReportPage = new CreateReportPage(page);

  // カスタムプロンプト
  const customExtraction = 'カスタム抽出プロンプト';
  const customInitialLabelling = 'カスタム初期ラベリングプロンプト';
  const customMergeLabelling = 'カスタム統合ラベリングプロンプト';
  const customOverview = 'カスタム要約プロンプト';

  // APIリクエストのインターセプト設定
  const requestPromise = interceptReportCreationRequest(page);

  // ページに移動
  await createReportPage.goto();

  // 基本情報の入力
  await createReportPage.fillBasicInfo('テストレポート', 'これはテストです');

  // AI設定を開く
  await createReportPage.openAISettings();

  // プロンプトを編集
  await createReportPage.editPrompts(
    customExtraction,
    customInitialLabelling,
    customMergeLabelling,
    customOverview
  );

  // フォーム送信
  await createReportPage.submitForm();

  // APIリクエストデータを取得
  const requestData = await requestPromise;

  // 編集したプロンプトがAPIリクエストに含まれていることを確認
  expect(requestData.extraction).toBe(customExtraction);
  expect(requestData.initialLabelling).toBe(customInitialLabelling);
  expect(requestData.mergeLabelling).toBe(customMergeLabelling);
  expect(requestData.overview).toBe(customOverview);
});
